import React, { Component, useState } from 'react';
import {
  View,
  Platform,
  TextInput,
  FlatList,
  Item,
  Text,
  StyleSheet
} from 'react-native';
import getAgenda from '../network/calendarAPI'

// API CALL

export default function Calendar(props) {
    //let data = getAgenda(defaultQueryArgs).then((response) => React.useState(response))
    let data=[
        {"dayOfTheWeek": "MA", "day": "fevr. 08", "timeIntervals": ['-', '-', '-', '17h30']}, 
        {"dayOfTheWeek": "ME", "day": "fevr 09",  "timeIntervals": ['9h00', '-', '-', '-']}, 
        {"dayOfTheWeek": "JE", "day": "fevr. 10", "timeIntervals": ['-', '15h30', '-', '-']}
        ];
    //const [useAgenda, setAgenda] = useState(true)
    const [useSelection, setSelection] = useState(data)
    console.log(useSelection);
    getAgenda(props.query).then((response) => setSelection(response))
    return (
      <View>
      <FlatList
      numColumns={7}
      data = {useSelection()}
      horizontal={false}
      renderItem={(test) => {
        return (
        <View style={styles.calendar}>
        <Text style={styles.jour}> {test.item.dayOfTheWeek} </Text>
        <Text style={styles.date}> {test.item.day} </Text>
        <Availabilities data={test.item.timeIntervals}/>
        </View>
        )
      }}
      />
      </View>
      )
}

function Availabilities(props) {
  return (
    <View>
    <FlatList
    data={props.data}
    horizontal={false}
    renderItem={(test) => {
      return (
        <Text style={styles.nobooking}> {test.item} </Text>
      )
    }}
    >
    </FlatList>
    </View>
  )
}

export class Welcome extends React.Component {
  render() {
    return <Text>Bonjour</Text>;
  }
}

const styles = StyleSheet.create({
  date: {
    backgroundColor: "white",
    borderWidth: 0,
    borderColor: "#333",
    padding: 0,
  },
  jour: {
    backgroundColor: "white",
    borderWidth: 0,
    borderBottomColor: "red",
    borderColor: "#333",
    padding: 0,
  },
  nobooking: {
    backgroundColor: "white",
    borderWidth: 0,
    borderBottomColor: "red",
    borderColor: "#333",
    padding: 0,
  },
  calendar: {
    fontFamily: "sans-serif",
    fontSize: "12"
  }
});
