import React, { Component, useState } from 'react';
import {
  View,
  Platform,
  TextInput,
  FlatList,
  Item,
  Text,
  StyleSheet,
  CheckBox,
  ViewPropTypes,
} from 'react-native';


export default function FirstRdv(props) {
    return (
        <View>
          <Text> Is this your first appointment with this practitioner?
          </Text>
          <Appointment style={styles.container}></Appointment>
        </View> 
    )
};


// to algin you need parent to have "inline-block" and children to be float

function Appointment(props) {
    const [useSelection, setSelection] = useState(true)
    const updateYesClick = () => setSelection(true)
    const updateNoClick = () => setSelection(false)
    return (
        <View style={styles.container}>
            <View style={[styles.checkBoxStyle, {"float": "left"}]}>
                <CheckBox style={[{"float": "left"}]}
                    value={useSelection? true:false}
                    onClick={updateYesClick}
                    >
                </CheckBox>
                <Text style={[{"float": "right"}]}> Yes </Text>
            </View>
            <View style={[styles.smallContainer, {"float": "right"}]}>
                <CheckBox style={[{"float": "left"}]}
                    value={useSelection? false:true}
                    onClick={updateNoClick}
                ></CheckBox>
                <Text style={[{"float": "right"}]}> No </Text>
            </View>
        </View>
    )

}

const styles = StyleSheet.create({
    container: {
        display: "inline-block",
    },
    checkBoxStyle: {
        display: "inline-block",
    } 
})
