import React, { Component, useState, useEffect } from 'react';
import {
  View,
  Platform,
  TextInput,
  FlatList,
  Item,
  Text,
  StyleSheet
} from 'react-native';

import getAgenda, {defaultQueryArgs} from './network/calendarAPI'

export default function Calendar(props) {
    const [useAgenda, setAgenda] = React.useState(props.data)
    console.log(useAgenda);
    React.useEffect(() => {
      getAgenda(defaultQueryArgs).then((response) => {setAgenda(response)}),
      []
    });
    return (
      <View>
      <FlatList
      numColumns={7}
      data = {useAgenda}
      horizontal={false}
      renderItem={(test) => {
        return (
        <View style={styles.calendar}>
        <Text style={styles.jour}> {test.item.dayOfTheWeek} </Text>
        <Text style={styles.date}> {test.item.day} </Text>
        <Availabilities data={test.item.timeIntervals}/>
        </View>
        )
      }}
      />
      </View>
      )
}

function Availabilities(props) {
  return (
    <View>
    <FlatList
    data={props.data}
    horizontal={false}
    renderItem={(test) => {
      return (
        <Text style={styles.nobooking}> {test.item} </Text>
      )
    }}
    >
    </FlatList>
    </View>
  )
}

const styles = StyleSheet.create({
  date: {
    backgroundColor: "white",
    borderWidth: 0,
    borderColor: "#333",
    padding: 0,
  },
  jour: {
    backgroundColor: "white",
    borderWidth: 0,
    borderBottomColor: "red",
    borderColor: "#333",
    padding: 0,
  },
  nobooking: {
    backgroundColor: "white",
    borderWidth: 0,
    borderBottomColor: "red",
    borderColor: "#333",
    padding: 0,
  },
  calendar: {
    fontFamily: "sans-serif",
    fontSize: "12"
  }
});
