import * as React from 'react';
import { Text, View, StyleSheet, Button } from 'react-native';
import Constants from 'expo-constants';

// or any pure javascript modules available in npm
import Calendar from './components/Calendar.js'
import FirstRdv from './components/FirstRdv.js';
import DropDown from './components/DropDown.js';

import getAgenda, {defaultQueryArgs} from './network/calendarAPI'

const [useSelectedTime, setSelectedTime] = React.useState('no_selection')
//// dummy data
let data=[
    {"dayOfTheWeek": "MA", "day": "fevr. 08", "timeIntervals": ['-', '-', '-', '17h30']}, 
    {"dayOfTheWeek": "ME", "day": "fevr 09",  "timeIntervals": ['9h00', '-', '-', '-']}, 
    {"dayOfTheWeek": "JE", "day": "fevr. 10", "timeIntervals": ['-', '15h30', '-', '-']}
    ];

export default function App() {
    // use state to define getter and setter for calendar data
    const [useAgenda, setAgenda] = React.useState([{"dayOfTheWeek": "", "day": "", "timeIntervals": ['']}])
    // use effect to avoid calling api at each render 
    React.useEffect(() => {
      //setAgenda(data)
      getAgenda(defaultQueryArgs).then((response: any) => {setAgenda(response); console.log(response)})
    }, []);
  return (
    <View style={styles.bigContainer}>
      <View style={styles.smallContainer}>
        <View>
          <Text> Find availability </Text>
        </View> 
        <FirstRdv setTime={setSelectedTime}></FirstRdv>
        <View>
          <DropDown></DropDown>
        </View> 
        <Calendar data={useAgenda}></Calendar>
        <View>
          <Button variant="text" title="Book Appointment"/>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  bigContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingTop: Constants.statusBarHeight,
    backgroundColor: '#FCF7F2',
    padding: 8,
  },
  smallContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingTop: Constants.statusBarHeight,
    backgroundColor: '#FFFFFF',
    padding: 8,
  },
});
