import * as React from 'react';
import { Text, View, StyleSheet, Button } from 'react-native';
import { useSetState } from 'react-use';
import Constants from 'expo-constants';

// or any pure javascript modules available in npm
import Calendar from './components/Calendar.js'
import FirstRdv from './components/FirstRdv.js';
import DropDown from './components/DropDown.js';

import getAgenda, {defaultQueryArgs} from './network/calendarAPI'

//// dummy data
//let data=[
    //{"dayOfTheWeek": "MA", "day": "fevr. 08", "timeIntervals": ['-', '-', '-', '17h30']}, 
    //{"dayOfTheWeek": "ME", "day": "fevr 09",  "timeIntervals": ['9h00', '-', '-', '-']}, 
    //{"dayOfTheWeek": "JE", "day": "fevr. 10", "timeIntervals": ['-', '15h30', '-', '-']}
    //];

export default function App() {

    const [state, setState] = useSetState({
      agenda: [{"dayOfTheWeek": "", "day": "", "timeIntervals": ['']}],
      selectedTime: '',
      msg: '',
      selectedReason: 'cultural',
      firstRdv: true,
    })
    console.log(state.agenda)

    // use state to define getter and setter for calendar data
    const [useAgenda, setAgenda] = React.useState([{"dayOfTheWeek": "", "day": "", "timeIntervals": ['']}])

    // use state to define and use selected time by the user
    const [useSelectedTime, setSelectedTime] = React.useState('')

    // message to display
    const [useMsg, setMsg] = React.useState('')

    // callback when user is confirming time of appointment
    const createAppointmentMsg = () => {
        setMsg(`Appointment booked for ${useSelectedTime}`)
    }

    // define and use the query based on the user inputs
    //const [useQuery, setQuery] = React.useState(defaultQueryArgs); 

    //const setQuery

    // use effect to avoid calling api at each render 
    React.useEffect(() => {
      //setAgenda(data)
      getAgenda(defaultQueryArgs).then((response: any) => {setAgenda(response); console.log(response)})
    }, []);

  return (
    <View style={styles.bigContainer}>
      <View style={styles.smallContainer}>
        <View>
          <Text> Find availability </Text>
        </View> 
        <FirstRdv query={setQuery}></FirstRdv>
        <View>
          <DropDown query={setQuery}></DropDown>
        </View> 
        <Calendar data={useAgenda} setTime={setSelectedTime}></Calendar>
        <View>
          <Button title={'Book Appointment'} onPress={() => createAppointmentMsg()}/>
        </View>
        <View>
          <Text> {useMsg} </Text>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  bigContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingTop: Constants.statusBarHeight,
    backgroundColor: '#FCF7F2',
    padding: 8,
  },
  smallContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingTop: Constants.statusBarHeight,
    backgroundColor: '#FFFFFF',
    padding: 8,
  },
});
